examples.source:
{
	block: 'b-icon',
	url: 'http://yastatic.net/lego/_/ExOJSanIrX2IAis4l5gO2uX6M1E.png',
	alt: 'My icon'
}

{
	block: 'b-icon',
	mods: { type: 'simple' },
	alt: 'My icon'
}

{
    block: 'b-layout-table',  // имя блока
    mods: { layout: '58-40'}, // модификаторы блока. Модификатор необязательный
    content: [                // структура страницы
        {
            elem: 'row',
            content: [
                {
                    elem: 'gap',
                    rowspan: '2'
                },
                {
                    elem: 'cell',
                    content: {
                        elem: 'inner',
                        content: 'Left cell'
                    }
                },
                {
                    elem: 'cell',
                    elemMods: { position: 'r'},
                    content: 'Right cell'
                }
            ]
        },
        {
            elem: 'row',
            content: [
                {
                    elem: 'cell',
                    colspan: '2',
                    content: 'Second row and one cell'
                }
            ]
        }
    ]
}

{
    block: 'b-logo',
    content: {
        elem: 'link',
        url: '/',
        title: 'logo',
        icon: {
            elem: 'icon',
            url: 'http://yastatic.net/lego/_/ExOJSanIrX2IAis4l5gO2uX6M1E.png',
            alt: 'logo'
        }
    }
}

{
  block: 'b-menu-horiz',
  title: {
    elem: 'title',
    content: 'Menu title'
  },
  content: [
    {
      elem: 'item',
      content: 'Home'
    },
    {
      elem: 'item',
      content: 'Office'
    }
  ]
}

{
  block: 'b-menu-horiz',
  mods: { layout: 'normal' },
  content: [
    {
      elem: 'item',
      content: 'Home'
    },
    {
      elem: 'item',
      content: 'Office'
    }
  ]
}

{
  block: 'b-menu-horiz',
  mods: { layout: 'normal' },
  title: {
    elem: 'title', content: 'Navigation:'
  },
  content: [
    {
      elem: 'item',
      elemMods: { state: 'current' },
      content: {
        block: 'b-link',
        mods: { pseudo: 'yes' },
        url: '/',
        content: 'Home'
      }
    }
  ]
}

{
  block: 'b-menu-horiz',
  js: false,
  mods: { layout: 'normal' },
  content: [
    {
      elem: 'item',
      content: {
        block: 'b-link', url: '/', content: 'Home'
      }
    },
    {
      elem: 'item',
      content: {
        block: 'b-link', url: '/', content: 'Office'
      }
    }
  ]
}

{
  block: 'b-menu-horiz',
  js: false,
  mods: { layout: 'normal' },
  content: [
    {
        elem: 'item',
        elemMods: { state: 'current' },
        content: 'Home'
    },
    {
      elem: 'item',
      content: 'Office'
    }
  ]
}

{
  block: 'b-menu-vert',
  content: [
    {
      elem: 'item',
      content: 'Index'
    },
    {
      elem: 'item',
      content: 'Contacts'
    }
  ]
}

{
  block: 'b-menu-vert',
  title: {
    elem: 'title',
    content: 'Menu title'
  },
  content: [
    {
      elem: 'item',
      content: 'Index'
    },
    {
      elem: 'item',
      content: 'Contacts'
    }
  ]
}

{
    block: 'b-page',
    title: 'Page with link',
    content: 'Page'
}

{
    block: 'b-page',
    title: 'Page with link',
    head: [
        { elem: 'css', url: 'example.css', ie: false },
        { elem: 'css', url: 'example.ie.css', ie: 'lt IE 8' },
        { elem: 'js', url: 'example.js' }
    ],
    content: 'Page'
}

{
    block: 'b-page',
    title: 'Page with link',
    head: [
        {
            elem: 'css',
            content: '.b-blah { color: #f00 }'
        }
    ],
    content: 'Page'
}

{
    block: 'b-page',
    title: 'Page with link',
    meta: {
        elem: 'meta',
        attrs: {
            name: 'keywords',
            content: 'js, css, html'
        }
    },
    content: 'Page'
}

{
    block: 'b-page',
    title: 'Page with link',
    meta: [
        {
            elem: 'meta',
            attrs: {
                name: 'keywords',
                content: 'js, css, html'
            }
        },
        {
            elem: 'meta',
            attrs: {
                name : 'description',
                content : 'Yet another webdev blog'
            }
        }
    ],
    content: 'Page'
}

{
    block: 'b-page',
    title: 'Page with link',
    content: {
        block: 'b-link',
        mods: { pseudo: 'yes', togcolor: 'yes', color: 'green' },
        url: '#',
        target: '_blank',
        title: 'Кликни меня',
        content: 'Псевдоссылка, меняющая цвет по клику'
    }
}

{
    block: 'b-search',
    attrs: { action: '/search.xml' },
    input: {
        elem: 'input'
    },
    button: {
        elem: 'button'
    },
    under: {
        block: 'b-link',
        url: '/',
        content: 'Advanced search'
    }
}

{
    block: 'b-page',
    title: 'Title of the page',
    head: [
        { elem: 'css', url: '_index.css' },
        { elem: 'meta', attrs: { name: 'description', content: 'description' }}
    ],
    content:[
        {
            block: 'some-block',
            content: 'Some content'
        },
        { block: 'i-jquery', mods: 'version' }
    ]
}
