examples.source:
{
    block : 'dropdown',
    mods : { switcher : 'link' },
    switcher : 'Ссылка',
    popup : 'Hello, world!'
}

{
    block : 'dropdown',
    mods : { switcher : 'link', theme : 'simple' },
    switcher : 'Ссылка',
    popup : 'Hello, world!'
}

{
    block : 'dropdown',
    mods : { switcher : 'link', theme : 'islands', size : 'm' },
    switcher : 'Ссылка',
    popup : 'Hello, world!'
}

{
    block : 'dropdown',
    mods : { switcher : 'button', theme : 'islands', size : 'm' },
    switcher : 'Кнопка',
    popup : 'Hello, world!'
}

{
    block : 'dropdown',
    mods : { switcher : 'button', theme : 'islands', size : 'm', disabled : true },
    switcher : 'Кнопка',
    popup : 'Hello, world!'
}

{
    block : 'dropdown',
    mods : { switcher : 'button', theme : 'islands', size : 'm', opened : true },
    switcher : 'Кнопка',
    popup : 'Hello, world!'
}

{
    block : 'dropdown',
    mods : { switcher : 'button', theme : 'islands', size : 'm' },
    switcher : {
        block : 'button',
        mods : { togglable : 'true' },
        text : 'custom button'
    },
    popup : 'Popup message'
}

{
    block : 'dropdown',
    mods : {
        switcher : 'Кнопка',
        theme : 'islands',
        size : 'xl'
    },
    switcher : 'Выпадающий список',
    popup : {
        block : 'menu',
        mods : {
            theme : 'islands',
            size : 'xl',
            select : 'check'
        },
        content : [
            {
                block : 'menu-item',
                val : 1,
                content : 'First item'
            },
            {
                block : 'menu-item',
                val : 2,
                content : 'Second item'
            }
        ]
    }
}

{
    block : 'dropdown',
    mods : { switcher : 'link' },
    switcher : 'Link',
    popup : 'Hello, world!'
}

{
    block : 'dropdown',
    mods : { switcher : 'link', theme : 'simple' },
    switcher : 'Link',
    popup : 'Hello, world!'
}

{
    block : 'dropdown',
    mods : { switcher : 'link', theme : 'islands', size : 'm' },
    switcher : 'Link',
    popup : 'Hello, world!'
}

{
    block : 'dropdown',
    mods : { switcher : 'button', theme : 'islands', size : 'm' },
    switcher : 'Button',
    popup : 'Hello, world!'
}

{
    block : 'dropdown',
    mods : { switcher : 'button', theme : 'islands', size : 'm', disabled : true },
    switcher : 'Button',
    popup : 'Hello, world!'
}

{
    block : 'dropdown',
    mods : { switcher : 'button', theme : 'islands', size : 'm' },
    switcher : {
        block : 'button',
        mods : { togglable : 'true' },
        text : 'custom button'
    },
    popup : 'Popup message'
}

{
    block : 'dropdown',
    mods : {
        switcher : 'Button',
        theme : 'islands',
        size : 'xl'
    },
    switcher : 'Dropdown menu',
    popup : {
        block : 'menu',
        mods : {
            theme : 'islands',
            size : 'xl',
            select : 'check'
        },
        content : [
            {
                block : 'menu-item',
                val : 1,
                content : 'First item'
            },
            {
                block : 'menu-item',
                val : 2,
                content : 'Second item'
            }
        ]
    }
}
